package javaBasics;

public class doWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int j = 20;

		do {
			System.out.println(j);
			j++;
		} while (j < 30);

		// Real scenerio for DoWhileLoop

		System.out.println("**********");

		int i = 20;

		do {
			System.out.println(i);
			i++;
		} while (i > 30); // 1 loop of execution is guaranteed

	}

}
