package javaBasics;

public class whileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// while loop
		// print 0 to 9 in forward order
		int i = 0;

		while (i < 10) {
			System.out.println(i);
			i++;
		}

		// print 10 to 1 in reverse order
		System.out.println("**********");

		int j = 10;

		while (j > 0) {
			System.out.println(j);
			j--;
		}

	}

}
